import BigInt
import EVM

struct Cool{
    static let byteCode: String = ""

    static let deployedByteCode: String = ""

    func sum(x: BigUInt, y: BigUInt) -> BigUInt {
        let query = ABI.encode()
        let out = try EVM.runQuery(bytecode: bytecode, query: query)

        return ABI.decode(out)
    }
}